import {Component, Input} from '@angular/core';
import {SchoolModel} from "../../model/school.model";
import {SchoolService} from "../school.service";
import {ActivatedRoute} from "@angular/router";

@Component({
  selector: 'app-school',
  templateUrl: './school.component.html',
  styleUrls: ['./school.component.css']
})
export class SchoolComponent {

  @Input()
  school: SchoolModel | undefined;

  constructor() {
  }
}
<div class="container">
<div class="card" >
  <div class="content">
    <ul>
      <h3 routerLink="/schools/{{school?.id}}">{{school?.name}}</h3>
      <li>Adresse: {{school?.address}} </li>
      <li>Etablissement: {{school?.type}} </li>
      <li>Tel: {{school?.phone}} </li>
    </ul>
  </div>
  <button type="button" class="btn btn-outline-primary" routerLink="/schools/param/{{school?.id}}">Parametrer</button>


  <ul class="icons">
    <li><a class ="linkedin" href="#"><i class="fa fa-phone"></i></a></li>
    <li><a class ="linkedin" href="#"><i class="fa fa-envelope-o"></i></a></li>
  </ul>
</div>
</div>
