import {Component, OnInit} from '@angular/core';
import {FormBuilder, FormGroup, Validators} from "@angular/forms";
import {SchoolModel} from "../../model/school.model";
import {SchoolService} from "../school.service";
import {ActivatedRoute, Router} from "@angular/router";
import {Subject} from "../../model/subject.model";

@Component({
  selector: 'app-school-param',
  templateUrl: './school-param.component.html',
  styleUrls: ['./school-param.component.css']
})
export class SchoolParamComponent implements OnInit{

  myForm!: FormGroup;
  myForm2!: FormGroup;
  school!: SchoolModel
  subject!:Subject
  formSubmitted: boolean = false

  constructor(private fb: FormBuilder,
              private sServ: SchoolService,
              private activatedRoute: ActivatedRoute,
              private router: Router){};

  submitForm(){
    this.formSubmitted = true
    if (this.myForm.valid) {
      if (!this.school) {
        this.sServ.add(this.myForm.value)
          .subscribe(s =>
            this.router.navigateByUrl('/schools'))
      } else {
        let schoolToUpdate = this.myForm.value
        console.log(schoolToUpdate)
        schoolToUpdate.id = this.school.id
        this.sServ.modify(this.myForm.value)
          .subscribe(s =>
            this.router.navigateByUrl('/schools'))
      }
    }
  };

  ngOnInit(): void {
    const id = this.activatedRoute.snapshot.paramMap.get("sId") || "";
    if (id != '') {
      this.sServ.findOne(+id).subscribe(v => {
        this.school = v
        this.myForm.get('name')?.setValue(this.school.name)
        this.myForm.get('address')?.setValue(this.school.address)
        this.myForm.get('type')?.setValue(this.school.type)
        this.myForm.get('phone')?.setValue(this.school.phone)
        this.myForm2.get('id')?.setValue(this.subject.id)
      })
    }


    this.myForm = this.fb.group({
      name: [this.school?.name || '', Validators.required],
      address: [this.school?.address || '', Validators.required],
      type: [this.school?.type || '', Validators.required],
      phone: [this.school?.phone || '', Validators.required],
      subjects: this.fb.array([
        this.myForm2 = this.fb.group({
          id: ['', Validators.required];
          color: ['white', Validators.required]
        })])
    })



  }
}
<br>
<div class="container d-flex h-100">
  <div class="row align-self-center">

    <div class="card-dek">
      <div class="card">
        <h1 class="card-header">Paramétrage</h1>
        <div class="text-danger">
          <ul *ngIf="formSubmitted">
            <li *ngIf="myForm.controls['name'].errors?.['required']">Nom requis</li>
            <li *ngIf="myForm.controls['address'].errors?.['required']">Une adresse est requise</li>
            <li *ngIf="myForm.controls['type'].errors?.['required']">Un type est requis</li>
            <li *ngIf="myForm.controls['phone'].errors?.['required']">Un numero de telephone est requis</li>
          </ul>
        </div>
        <form [formGroup]="myForm" (ngSubmit)="submitForm()">
          <div class="card-body">
            <div class="form-text">
              <label for="name">Nom de l'établissement</label>
              <input type="text" id="name" formControlName="name">
            </div>
            <div class="form-text">
              <label for="address">Adresse</label>
              <input type="text" id="address" formControlName="address">
            </div>
            <div class="group">
              <label class="form-text" for="type">Type Etablissement</label>
              <select id="type" formControlName="type">
                <option value='COLLEGE'>College</option>
                <option value='LYCEE'>Lycee</option>
              </select>
            </div>
            <div class="form-text">
              <label for="phone">Téléphone</label>
              <input type="text" id="phone" formControlName="phone">
            </div>
          </div>
          <div class="card-footer">
            <button class="btn btn-outline-info" *ngIf="!school" type="submit">Ajouter</button>
            <button class="btn btn-outline-primary" *ngIf="school" type="submit">Modifier</button>
            <button class="btn btn-outline-info" routerLink="../../">Retour</button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>
