import { Component } from '@angular/core';
import {filter, map} from "rxjs";
import {ActivationEnd, Router} from "@angular/router";

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.css']
})
export class MenuComponent {
  isMenuOpen = false;

  toggleMenu():void{
    this.isMenuOpen =! this.isMenuOpen
  }

  title: string|undefined;

  sId:any;
  constructor(private router: Router) {
    this.router.events
      .pipe(
        filter(e => (e instanceof ActivationEnd) && (Object.keys(e.snapshot.params).length > 0)),
        map(e => e instanceof ActivationEnd ? e.snapshot.params : {})
      )
      .subscribe(params => {
        this.sId = Object.values(params).toString();
        this.sId = this.sId[0];
        console.log(this.sId)
      });
  }

}
<header>

  <nav class="navbar">
    <a href="#" class="logo">Team1</a>
    <div class="navlinks" [class.is-open]="isMenuOpen">
      <ul>
        <li class="home"><a href="schools">Home</a></li>
        <li><a href="schools/{{sId}}/klasses">Liste des classes</a></li>
        <li><a href="schools/{{sId}}/teachers">Liste des professeurs</a></li>
        <li><a href="schools/{{sId}}/subjects">Liste des matières</a></li>
        <li><a href="schools/{{sId}}/rooms">Liste des salles de classe</a></li>
      </ul>
    </div>
    <img src="assets/images/menu-btn.png" class="menu-hamburger" (click)="toggleMenu()">
  </nav>

</header>
