        <label for="subject1" >Matière enseignée</label>
        <select id="subject1" formControlName="id">
          {{schoolId}}
          <option class="lastName" *ngFor="let s of subjects" [value]="s.id">{{s.name}}</option>
        </select>
      </div><!--
        <div class="form-text">
          <label for="name1">Nom</label>
          <input type="text" id="name1" formControlName="name">
        </div>
        <div class="form-text">
          <label for="color1">Nom</label>
          <input type="text" id="color1" formControlName="color">
        </div>
        <div class="form-text">
          <label for="school1">Nom</label>
          <input type="text" id="school1" formControlName="school">
        </div>-->
      </form>
      <form [formGroup]="myForm4" (ngSubmit)="submitForm()">
        <div class="form-text"  >
          <label for="subject2" >Matière enseignée</label>
          <select id="subject2" formControlName="id">
            {{schoolId}}
            <option class="lastName" *ngFor="let s of subjects" [value]="s.id">{{s.name}}</option>
          </select>
        </div>
        <!--
        <div class="form-text">
          <label for="name2">Nom</label>
          <input type="text" id="name2" formControlName="name">
        </div>
        <div class="form-text">
          <label for="color2">Nom</label>
          <input type="text" id="color2" formControlName="color">
        </div>
        <div class="form-text">
          <label for="school2">Nom</label>
          <input type="text" id="school2" formControlName="school">
        </div>-->
      </form>
      <form [formGroup]="myForm5" (ngSubmit)="submitForm()">
        <div class="form-text"  >
          <label for="subject3" >Matière enseignée</label>
          <select id="subject3" formControlName="id">
            {{schoolId}}
            <option class="lastName" *ngFor="let s of subjects" [value]="s.id">{{s.name}}</option>
          </select>
        </div>
        <!--<div class="form-text">
          <label for="name3">Nom</label>
          <input type="text" id="name3" formControlName="name">
        </div>
        <div class="form-text">
          <label for="color3">Nom</label>
          <input type="text" id="color3" formControlName="color">
        </div>
        <div class="form-text">
          <label for="school3">Nom</label>
          <input type="text" id="school3" formControlName="school">
        </div> -->
      </form>


    </div>
    <div class="card-footer">
      <button class="btn btn-outline-info" type="submit">Ajouter</button>
      <button type="button" class="btn btn-outline-info" routerLink="../../">Retour</button>
    </div>
  </form>

  {{myForm.value | json}}

</div>
