import {AfterContentInit, AfterViewInit, Component, ElementRef, Input, OnInit, ViewChild} from '@angular/core';
import {CalendarOptions} from "@fullcalendar/core";
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin, {Draggable} from '@fullcalendar/interaction';
import {NgbModalRef} from "@ng-bootstrap/ng-bootstrap";
import {BsModalService, BsModalRef} from "ngx-bootstrap/modal";

@Component({

  selector: 'app-calendar',
  templateUrl: './calendar.component.html',
  styleUrls: ['./calendar.component.css']
})
export class CalendarComponent implements OnInit, AfterViewInit {

  currentModal: NgbModalRef | undefined
  modalRef?: BsModalRef;

  title:any;

  start : any;
  end: any;

  day : string = '';
  @Input() startInput : any;
  @Input() endInput: any;


  events: any = [{
    "title": "Philo",
    "startTime": "09:30",
    "endTime": "13:00",
    "backgroundColor": "orange",
    "daysOfWeek": ['1']
  },
    {
      "title": "Musique",
      "startTime": "15:30:00",
      "endTime": "17:00:00",
      "backgroundColor": "salmon",
      "daysOfWeek": ['5']
    },
    {
      "title": "Arts Plastique",
      "startTime": "10:30:00",
      "endTime": "16:00:00",
      "backgroundColor": "Lavender",
      "daysOfWeek": ['3']
    }]

  container: any = this.events[0];

  calendarOptions: CalendarOptions = {
    plugins: [dayGridPlugin, timeGridPlugin, interactionPlugin],
    headerToolbar: {
      left: 'prev today',
      center: 'title',
      right: 'next'
    },
    buttonText: {
      today: 'Aujourd\'hui'
    },
    initialView: 'timeGridWeek',
    weekends: false,
    nowIndicator: true,
    slotMinTime: "08:00:00",
    slotMaxTime: "21:00:00",
    locale: 'fr',
    editable: true,
    droppable: true,
    selectable: true,
    eventSources: [{events: this.events}],
    eventClick: this.handleDateClick.bind(this),
    select:this.addEventClick.bind(this)
  };

  config ={
    animated: true
  };
  @ViewChild('templateInfo') templateInfo!: string;

  @ViewChild('templateAdd') templateAdd!:string;

  constructor(private modalService:BsModalService){
  }


  ngOnInit():void{

  }

  handleDateClick(arg:any){
    console.log(arg);
    console.log(arg.event._def.title);
  this.title = arg.event._def.title;
    this.start= arg.event.start;
    this.end = arg.event.end;
    this.day = arg.event._def.recurringDef.typeData.daysOfWeek[0];
  switch(arg.event._def.recurringDef.typeData.daysOfWeek[0])
  {
    case '1': {
      this.day = "Lundi";
      break;
    }
    case '2': {
      this.day = "Mardi";
      break;
    }
  case '3': {
      this.day = "Mercredi";
      break;
    }
  case '4': {
      this.day = "Jeudi";
      break;
    }
  case '5': {
      this.day = "Vendredi";
      break;
    }
    default:
      {
        //statements;
        break;
      }
    }

    this.modalRef = this.modalService.show(this.templateInfo, this.config);
  }

 addEventClick(ard:any):void{
 //  this.modalRef = this.modalService.show('input[name=start]');
 //   this.modalRef = this.modalService.find('input[name=evtEnd]').val(
 //     this.end.format('YYYY-MM-DD HH:mm:ss')
 //   );
 //   show modal dialog
   this.modalRef = this.modalService.show(this.templateAdd, this.config);}

  ngAfterViewInit(): void {
    this.container = new ElementRef('external');
    new Draggable(this.container.nativeElement, {
      itemSelector: '.fc-event',
      eventData: (eventEl) => {
        console.log(eventEl);
        this.title = eventEl.getAttribute("title");

        return {
          title: eventEl.innerText,
        };
      },
    });
  }
}
<div id="external-event" [style.margin-right]="'50px'" [style.margin-left]="'50px'">

  <h2 class="card card-header">Liste des cours</h2>
  <div class="external-event-list">
    <div *ngIf="events.length > 0">
      <div class="fc-event" *ngFor="let ev of events" #external>
        <div class="card" cdkDrag>{{ev.title}}</div>
      </div>
    </div>
  </div>
  <br>

  <full-calendar [options]="calendarOptions"></full-calendar>
  <ng-template #templateInfo>
    <div class="modal-header">
      <h4 class="modal-title pull-left">{{title}}</h4>
      <button type="button" class="btn-close close pull-right"
              aria-label="Close" (click)="modalRef?.hide()">
        <span aria-hidden="true" class="visually-hidden">&times;</span>
      </button>
    </div>
    <div class="modal-body">
      <form name="save-event" method="post">
        <div class="form-group">
          <label for="title">Matière :</label>
          <input type="text" name="title" [placeholder]="title" id="title">
        </div>
        <div class="form-group">
          <label for="start">Début : </label>
          <input type="text" name="start" [placeholder]="[start|date :'shortTime']" id="start">
        </div>
        <div class="form-group">
              <label for="end">Fin : </label>
        <input type="text" name="end" [placeholder]="[end|date :'shortTime']" id="end">
        </div>
    <div class="form-group">
        <label for="day">Jour : </label>
        <input type="text" name="day" [placeholder]="day" id="day">
    </div>
    </form>

</div>
<div class="modal-footer">
  <button type="button" class="btn btn-default" data-dismiss="modal" (click)="modalRef?.hide()">Close</button>
  <button type="submit" class="btn btn-primary">Modifier Planning</button>
</div>
</ng-template>


<ng-template #templateAdd>
  <div class="modal-header">
    <h4 class="modal-title pull-left">Ajouter un cours</h4>
    <button type="button" class="btn-close close pull-right"
            aria-label="Close" (click)="modalRef?.hide()">
      <span aria-hidden="true" class="visually-hidden">&times;</span>
    </button>
  </div>
  <div class="modal-body">
    <form name="save-event" method="post">
      <div class="form-group">
          <label for="emptyTitle">Matière :</label>
          <input type="text" name="title" placeholder="Matière" id="emptyTitle">
      </div>
      <div class="form-group">
          <label for="emptyStart">Début : </label>
          <input type="text" name="start" placeholder="Heure de Début" id="emptyStart">
      </div>
      <div class="form-group">
          <label for="emptyEnd">Fin : </label>
          <input type="text" name="end" placeholder="Heure de fin" id="emptyEnd">
      </div>
      <div class="form-group">
          <label for="emptyDay">Jour : </label>
          <input type="text" name="day" placeholder="Jour" id="emptyDay"><br>
      </div>
    </form>

  </div>
  <div class="modal-footer">
    <button type="button" class="btn btn-default" data-dismiss="modal" (click)="modalRef?.hide()">Fermer</button>
    <button type="submit" class="btn btn-primary">Ajouter</button>
  </div>
</ng-template>
</div>
